select * from (select s.id, s.supplier, s.contacts, s.telephone, s.phone_num, s.email, -- 起始期初金额 (case when s.type='供应商' then ifnull(s.begin_need_pay,0) else ifnull(s.begin_need_get,0) end) begin_need, -- 上期欠款金额 (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preDebtMoney, -- 上期退货的欠款金额 (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preReturnDebtMoney, -- 上期收付款 (select (case when ah.type='收款' then ifnull(sum(ai.each_amount),0) else 0-ifnull(sum(ai.each_amount),0) end) from jsh_account_item ai left join jsh_account_head ah on ai.header_id=ah.id and ifnull(ah.delete_flag,'0') !='1' where ah.organ_id=s.id and ah.type=#{billType} and ah.bill_time <= #{beginTime} and ifnull(ai.delete_flag,'0') !='1') preBackMoney, -- 本期欠款 (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') debtMoney, -- 本期退货的欠款金额 (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') returnDebtMoney, -- 本期收付款 (select (case when ah.type='收款' then ifnull(sum(ai.each_amount),0) else 0-ifnull(sum(ai.each_amount),0) end) from jsh_account_item ai left join jsh_account_head ah on ai.header_id=ah.id and ifnull(ah.delete_flag,'0') !='1' where ah.organ_id=s.id and ah.type=#{billType} and ah.bill_time>#{beginTime} and ah.bill_time <= #{endTime} and ifnull(ai.delete_flag,'0') !='1') backMoney from jsh_supplier s where s.enabled=1 and s.type=#{supplierType}
and s.id = #{organId}
and s.id in (
#{organId}
)
and ifnull(s.delete_flag,'0') !='1') tb where begin_need+preDebtMoney-preReturnDebtMoney-preBackMoney+debtMoney-returnDebtMoney-backMoney!=0 order by begin_need+preDebtMoney-preReturnDebtMoney-preBackMoney+debtMoney-returnDebtMoney-backMoney desc
limit #{offset},#{rows}

select count(1) from (select s.id, s.supplier, s.contacts, s.telephone, s.phone_num, s.email, -- 起始期初金额 (case when s.type='供应商' then ifnull(s.begin_need_pay,0) else ifnull(s.begin_need_get,0) end) begin_need, -- 上期欠款金额 (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preDebtMoney, -- 上期退货的欠款金额 (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preReturnDebtMoney, -- 上期收付款 (select (case when ah.type='收款' then ifnull(sum(ai.each_amount),0) else 0-ifnull(sum(ai.each_amount),0) end) from jsh_account_item ai left join jsh_account_head ah on ai.header_id=ah.id and ifnull(ah.delete_flag,'0') !='1' where ah.organ_id=s.id and ah.type=#{billType} and ah.bill_time <= #{beginTime} and ifnull(ai.delete_flag,'0') !='1') preBackMoney, -- 本期欠款 (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') debtMoney, -- 本期退货的欠款金额 (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') returnDebtMoney, -- 本期收付款 (select (case when ah.type='收款' then ifnull(sum(ai.each_amount),0) else 0-ifnull(sum(ai.each_amount),0) end) from jsh_account_item ai left join jsh_account_head ah on ai.header_id=ah.id and ifnull(ah.delete_flag,'0') !='1' where ah.organ_id=s.id and ah.type=#{billType} and ah.bill_time>#{beginTime} and ah.bill_time <= #{endTime} and ifnull(ai.delete_flag,'0') !='1') backMoney from jsh_supplier s where s.enabled=1 and s.type=#{supplierType}
and s.id = #{organId}
and s.id in (
#{organId}
)
and ifnull(s.delete_flag,'0') !='1') tb where begin_need+preDebtMoney-preReturnDebtMoney-preBackMoney+debtMoney-returnDebtMoney-backMoney!=0

select sum(begin_need) begin_need, sum(preDebtMoney) preDebtMoney, sum(preReturnDebtMoney) preReturnDebtMoney, sum(preBackMoney) preBackMoney, sum(debtMoney) debtMoney, sum(returnDebtMoney) returnDebtMoney, sum(backMoney) backMoney from (select s.id, (case when s.type='供应商' then ifnull(s.begin_need_pay,0) else ifnull(s.begin_need_get,0) end) begin_need, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preDebtMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preReturnDebtMoney, (select (case when ah.type='收款' then ifnull(sum(ai.each_amount),0) else 0-ifnull(sum(ai.each_amount),0) end) from jsh_account_item ai left join jsh_account_head ah on ai.header_id=ah.id and ifnull(ah.delete_flag,'0') !='1' where ah.organ_id=s.id and ah.type=#{billType} and ah.bill_time <= #{beginTime} and ifnull(ai.delete_flag,'0') !='1') preBackMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') debtMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') returnDebtMoney, (select (case when ah.type='收款' then ifnull(sum(ai.each_amount),0) else 0-ifnull(sum(ai.each_amount),0) end) from jsh_account_item ai left join jsh_account_head ah on ai.header_id=ah.id and ifnull(ah.delete_flag,'0') !='1' where ah.organ_id=s.id and ah.type=#{billType} and ah.bill_time>#{beginTime} and ah.bill_time <= #{endTime} and ifnull(ai.delete_flag,'0') !='1') backMoney from jsh_supplier s where s.enabled=1 and s.type=#{supplierType}
and s.id = #{organId}
and s.id in (
#{organId}
)
and ifnull(s.delete_flag,'0') !='1') tb where begin_need+preDebtMoney-preReturnDebtMoney-preBackMoney+debtMoney-returnDebtMoney-backMoney!=0

select dh.*, concat(dh.sub_type,dh.type) as depotHeadType, a.name AccountName, s.supplier OrganName, s.contacts, s.telephone, s.address, s1.supplier supplierName from jsh_depot_head dh left join jsh_supplier s on dh.organ_id=s.id and ifnull(s.delete_Flag,'0') !='1' left join jsh_account a on dh.account_id=a.id and ifnull(a.delete_Flag,'0') !='1' left join jsh_supplier s1 on dh.supplier_id=s1.id and ifnull(s1.delete_Flag,'0') !='1' where 1=1
and dh.number=#{number}
and ifnull(dh.delete_flag,'0') !='1'

