where 1=1
and di.id is not null

    and dh.type=#{type}


    and dh.sub_type=#{subType}


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}


    and dh.status = '1'


    and dh.creator in (

