select distinct dh.*, m.img_name mImgName, m.name mName, di.material_unit mMaterialUnit, di.oper_number operNumber,sp.project_name projectName,me.bar_code mBarCode from jsh_depot_head dh left join jsh_depot_item di on dh.id = di.header_id and ifnull(di.delete_flag, '0') != '1' left join jsh_material m on di.material_id = m.id and ifnull(m.delete_flag, '0') != '1' left join jsh_material_extend me on m.id = me.material_id and ifnull(me.delete_Flag, '0') != '1' left join supplier_serve_project ssp on dh.number = ssp.number left join serve_project sp on ssp.serve_project_id = sp.id where 1=1 and (dh.type='其它') -- and (dh.sub_type='寄存订单' or dh.sub_type='销售寄存订单') and dh.sub_type=#{subType} and dh.supplier_id =#{supplierId}
and dh.status in (
#{status}
)
and ifnull(dh.delete_flag,'0') !='1' order by dh.oper_time desc
limit #{offset},#{rows}

select count(1) from ( select distinct dh.*, m.img_name mImgName, m.name mName, di.material_unit mMaterialUnit, di.oper_number operNumber,sp.project_name projectName,me.bar_code mBarCode from jsh_depot_head dh left join jsh_depot_item di on dh.id = di.header_id and ifnull(di.delete_flag, '0') != '1' left join jsh_material m on di.material_id = m.id and ifnull(m.delete_flag, '0') != '1' left join jsh_material_extend me on m.id = me.material_id and ifnull(me.delete_Flag, '0') != '1' left join supplier_serve_project ssp on dh.number = ssp.number left join serve_project sp on ssp.serve_project_id = sp.id where 1=1 and (dh.type='其它') -- and (dh.sub_type='寄存订单' or dh.sub_type='销售寄存订单') and dh.sub_type=#{subType} and dh.supplier_id =#{supplierId}
and dh.status in (
#{status}
)
and ifnull(dh.delete_flag,'0') !='1' order by dh.oper_time desc) tb

select ifnull(sum(tb.totalPrice),0) as totalAmount
from (select pr.total_price totalPrice
from jsh_depot_head dh
left join price_receipts pr on dh.number = pr.number
and dh.type=#{type}
and dh.sub_type=#{subType}


    and (dh.supplier_id=#{supplierId} or dh.organ_id=#{supplierId})


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}

and dh.status=1
and pr.status=1
and ifnull(dh.delete_flag,'0')!='1'
and ifnull(pr.delete_flag,'0')!='1'
)as tb

select dh.id
from jsh_depot_head dh
where 1=1

    and dh.organ_id=#{supplierId}


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}

and ifnull(dh.delete_flag,'0')!='1'

select
       count(case when dh.sub_type = '寄存入库' and dh.type = '入库' then 1 end)as  inCount,
       count(case when dh.sub_type = '销售寄存出库' and dh.type = '出库' then 1 end)as outCount
from jsh_depot_head dh
where 1 = 1

    and (dh.supplier_id=#{supplierId} or dh.organ_id=#{supplierId})


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}

and dh.status='1'
and ifnull(dh.delete_flag,'0') !='1';
